      out.write("	\n");
      out.write("	const stars = document.querySelectorAll('.star');\n");
      out.write("    const ratingInput = document.getElementById('rating');\n");
      out.write("\n");
      out.write("    stars.forEach((star, idx) => {\n");
      out.write("      star.addEventListener('mouseover', () => {\n");
      out.write("        stars.forEach((s, i) => {\n");
      out.write("          s.classList.toggle('hovered', i <= idx);\n");
      out.write("        });\n");
      out.write("      });\n");
      out.write("\n");
